INSERT INTO escenas VALUES('1','2','img/escena2.png','Famosa escena de baile entre John Travolta y Uma Thurman');
INSERT INTO recomendaciones VALUES('1','1','2');
INSERT INTO recomendaciones VALUES('1','2','3');
