INSERT INTO premios VALUES('2','1','1972: 3 Oscars: Mejor película, Actor (Marlon Brando), Guión adaptado');
INSERT INTO premios VALUES('2','2','1972: 5 Globos de Oro: Película (Drama), Director, Actor (Brando), Guión y BSO');
INSERT INTO premios VALUES('2','3','1972: Premios BAFTA: Mejor música. 5 nominaciones, incluyendo Mejor actor (Brando)');
INSERT INTO premios VALUES('2','4','1972: Círculo de Críticos de Nueva York: Mejor actor secundario (Duvall). 4 nominaciones');
INSERT INTO premios VALUES('2','5','1972: National Board of Review: Mejor actor sec. (Pacino) y Mejores 10 films del año');
INSERT INTO premios VALUES('2','6','Sindicato de Directores (DGA): Mejor director');
INSERT INTO criticas VALUES('2','1','Carlos Boyero','"El padrino" son palabras mayores. Las dos primeras partes están entre las 10 mejores películas de la historia del cine.');
INSERT INTO criticas VALUES('2','2','Luis Martínez','Coppola inventa una nueva mirada para el cine y amplía los horizontes de una industria que pedía a gritos savia nueva.');
